package javaassignments;

public class divisibleByFive {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int b= 300;
		if(b%5 == 0) {
			System.out.println(b + " is divisible by 5");
		}else if(b%5 !=0) {
			System.out.println(b + " is not divisible by 5");
		}
	}

}
