package javaassignments;

public class evenOrOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a= 10;
		if(a%2 == 0) {
			System.out.println(a + " is Even Number");
		}else if(a%2 !=0) {
			System.out.println(a + " is Even Number");
		}

	}

}
