package javaassignments;

public class methodStatic {
	//static method
	static void show(int x) {
		System.out.println("Welcome to  int prameter");
	}
	
	static void show(double x) {
		System.out.println("Welcome to double prameter");
	
	}
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		show(6);
	}
 // it will provide the flexibility to the user, so  user can use one method to perform different operation
}
